<template>
    <el-container class="container--login" id="app">
        <!-- <el-header>
            <div class="container">
                <h1>
                    <router-link to="/">
                        <svg-icon type="dx-logo" className="skye-logo" />
                    </router-link>
                </h1>
            </div>
        </el-header> -->
        <div class="main-content">
            <div class="container">
                <el-form ref="form" :model="form" :rules="rules">
                    <div class="logo"><svg-icon type="dx-logo" className="svg"/></div>
                    <el-form-item prop="username">
                        <el-input v-model="form.username" placeholder="账户名称"></el-input>
                    </el-form-item>
                    <el-form-item prop="password">
                        <el-input type="password" placeholder="账户密码" v-model="form.password">
                            <a slot="append" class="forget" href="javascript:;">忘记密码？</a>
                        </el-input>
                    </el-form-item>
                    <!-- <el-form-item prop="captchaImage" class="captcha-image">
                            <el-input placeholder="请输入右边图片验证码" v-model="form.captchaImage">
                                <img slot="append" src="data:image/png;base64,"
                                    alt="" id="captcha-image">
                            </el-input>
                        </el-form-item> -->
                    <el-form-item>
                        <el-button type="primary" @click="submitForm('form')" v-loading.fullscreen.lock="fullscreenLoading">立即登录</el-button>
                    </el-form-item>
                </el-form>
            </div>
            <div class="copyright">© 2018 权限管理快速构建平台 版权所有 复星金服</div>
        </div>
    </el-container>
</template>

<script>
export default {
  name: "user-login",
  data() {
    return {
      form: {
        username: "admin",
        password: "123456"
        // captchaImage: ""
      },
      rules: {
        username: [
          {
            required: true,
            message: "请输入账户名称",
            trigger: "blur"
          }
        ]
      },
      fullscreenLoading: false
    };
  },
  methods: {
    submitForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          this.fullscreenLoading = true;
          this.$store
            .dispatch("login", this.form)
            .then(() => {
              this.fullscreenLoading = false;
              if (this.$route.query.redirect) {
                let redirect = decodeURIComponent(this.$route.query.redirect);
                let hash = decodeURIComponent(this.$route.hash);
                console.log("redirect", redirect);
                console.log("hash", hash);
                window.location.href = redirect + hash;
              } else {
                window.location.href = "/";
              }
            })
            .catch(() => {
              this.fullscreenLoading = false;
            });
        } else {
          console.log("error submit!!");
          return false;
        }
      });
    }
  }
};
</script>